func countWaysToWin(time, record int) int {
	waysToWin := 0
	for i := 0; i < time; i++ {
		speed := i
		travelTime := time - i
		distance := speed * travelTime
		if distance > record {
			waysToWin++
		}
	}
	return waysToWin
}

