	defer file.Close()

	scanner := bufio.NewScanner(file)
	product := 1
	for scanner.Scan() {
		line := scanner.Text()
		time, record := parseLine(line)
		waysToWin := countWaysToWin(time, record)
		product *= waysToWin
	}

	if err := scanner.Err(); err != nil {
		fmt.Println("Error reading input file:", err)
		return
	}

	fmt.Println(product)
}
